mapper.select:
WITH ORDEREDACTIVE AS( SELECT cl_no, cl_empid, cl_name, cl_charger, cl_tel, cl_mail, cl_detail, cl_bank, cl_receive, cl_add1, cl_add2, cl_items, cl_bankno, cl_depository, cl_sort, cl_delete FROM client
ORDER BY CL_NO DESC ) SELECT * FROM( SELECT ROWNUM RNUM, ORDEREDACTIVE.* FROM ORDEREDACTIVE )A
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

mapper.update:
UPDATE client
    SET
        cl_name= #{cl_name}
        ,cl_charger = #{cl_charger,jdbcType=VARCHAR}
        ,cl_tel = #{cl_tel}
        ,cl_mail = #{cl_mail}
        ,cl_detail = #{cl_detail,jdbcType=VARCHAR}
        ,cl_bank = #{cl_bank,jdbcType=VARCHAR}
        ,cl_receive = #{cl_receive,jdbcType=VARCHAR}
        ,cl_add1= #{cl_add1,jdbcType=VARCHAR}
        ,cl_add2 = #{cl_add2,jdbcType=VARCHAR}
        ,cl_items = #{cl_items,jdbcType=VARCHAR}
        ,cl_bankno = #{cl_bankno,jdbcType=VARCHAR}
        ,cl_depository = #{cl_depository,jdbcType=VARCHAR}
        ,cl_zip = #{cl_zip,jdbcType=NUMERIC}
WHERE
    cl_no = #{cl_no}
AND 
	cl_empid = #{cl_empid}

UPDATE client
    SET
     cl_delete = 'Y'
WHERE
    cl_no = #{cl_no}
    AND   cl_empid = #{cl_empid}
